mapper.select:
SELECT 
FROM tbl_flow_model_info


        AND model_name LIKE concat('%', #{modelName}, '%')


        AND model_key = #{modelKey}


        AND category = #{category}


        AND model_status = #{status}


        AND del_flag = #{delFlag}


ORDER BY create_time DESC

mapper.insert:
INSERT INTO tbl_flow_model_info (
    model_key, model_name, category, model_status, version, form_id, form_key,
    deploy_id, definition_id, definition_key, definition_name, model_content, model_editor_json,
    deploy_time, create_by, create_time, update_by, update_time, remark, del_flag
) VALUES (
    #{modelKey}, #{modelName}, #{category}, #{status}, #{version}, #{formId}, #{formKey},
    #{deployId}, #{definitionId}, #{definitionKey}, #{definitionName}, #{modelContent}, #{modelEditorJson},
    #{deployTime}, #{createBy}, #{createTime}, #{updateBy}, #{updateTime}, #{remark}, #{delFlag}
)

mapper.update:
UPDATE tbl_flow_model_info
model_key = #{modelKey}, model_name = #{modelName}, category = #{category}, model_status = #{status}, version = #{version}, form_id = #{formId}, form_key = #{formKey}, deploy_id = #{deployId}, definition_id = #{definitionId}, definition_key = #{definitionKey}, definition_name = #{definitionName}, model_content = #{modelContent}, model_editor_json = #{modelEditorJson}, deploy_time = #{deployTime}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, del_flag = #{delFlag},
WHERE model_id = #{modelId}
